sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var field = g_form.getValue('field');
    var look = new GlideAjax("x_1438983_coe_app.LookupData");
    look.addParam('sysparm_name', "getLookupDetails");
    look.addParam('sysparm_field', field);
    look.getXML(callback);

    function callback(response) {
        var ans = response.responseXML.documentElement.getAttribute("answer");
        if (ans != "1") {
			//alert(ans);
			var value = parseInt(ans)+1;

            g_form.setValue("value", value);

        }
    }

    //Type appropriate comment here, and begin script below

}
