sys_script_include.script:
var LookupData = Class.create();
LookupData.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getLookupDetails: function() {
        var gr = new GlideRecord("x_1438983_coe_app_lookup_details");
        var lookField = this.getParameter('sysparm_field');
        gr.addEncodedQuery("field=" + lookField);
        gr.query();
        if (gr.next()) {
            // var uObj = {};
            // uObj.value = gr.value.toString();
            var gh = [];
            while (gr.next()) {
                gh.push(parseInt(gr.value));
            }
			var max=Math.max(...gh);
            return max;
        } else {
            return "1";
        }


    },

    type: 'LookupData'
});
