sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    /**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
async function handler({
    api,
    event,
    helpers,
    imports
}) {

    let  {create_new_kb} = api.state.translatedText;

    api.emit("NAV_ITEM_SELECTED", {
        route: 'record',
        fields: {
            table: "kb_knowledge",
            sysId: "-1"
        },
        params: null,
        redirect: null,
        passiveNavigation: null,
        title: create_new_kb,
        multiInstField: "sysId",
        targetRoute: null,
        external: null
    })
}

}
